function checkIfPlaceIsBar(place) {

    for(var i = 0; i < place.types.length; i++)
    {

        if ( place.types[i] === "bar" )
            return true;

    }

    return false;

}

function checkIfPlaceIsRestaurant(place) {

    for(var i = 0; i < place.types.length; i++)
    {

        if ( place.types[i] === "restaurant" )
            return true;

    }

    return false;

}
